import React, { Component } from "react";
import "./presentationlist.css";
class Presentationlist extends Component {
  state = {
    style: [
      {
        id: 0
      }
    ]
  };
  styleRow = () => {
    const style = { ...this.state.style };
    style[0].id = 1;
    this.setState({ style: style });
  };
  styleColumn = () => {
    const style = { ...this.state.style };
    style[0].id = 0;
    this.setState({ style: style });
  };
  render() {
    return (
      <div className="mainpresentation">
        <div>
          <p onClick={() => this.styleRow()}>Row</p>
          <p>Column</p>
        </div>
        {this.props.presentation.posts.map(item => {
          return (
            <div key={item.id} className="hero">
              <div
                className={
                  this.props.presentation.classnames[this.state.style[0].id]
                    .name
                }
              >
                <p>Presenter : {item.presentatorname}</p>
                <p>Evaluator: {item.evaluatorname}</p>
                <p>Topic: {item.topic}</p>
                <p>Topic: {item.url}</p>
                <p>Comments: {item.comments}</p>
              </div>
              <div>
                <button className="btn-view">View</button>
                <button className="btn-edit">Edit</button>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

export default Presentationlist;
